package Tokenizers.Lab2;//<ASSIGN> ::= <ID> = <EXP>;public class Assign {	private final Tokenizer t;	private Id id;	private final Exp exp;		private static final int EQL = 14; 	private static final int SCOL = 12; 		public Assign() {		t = Tokenizer.getInstance();		exp = new Exp();	}		public void parseAssign() {		id = Id.parseId();	if ( EQL != t.getToken() ) { System.out.println("\nmissing = in assignment"); System.exit(0); }	t.skipToken(); /*=*/	exp.parseExp();	if ( SCOL != t.getToken() ) { System.out.println("\nmissing ; at end of assignment"); System.exit(0); }	t.skipToken(); /*;*/	}		public void printAssign() {		id.printId();		System.out.print("= ");		exp.printExp();		System.out.println("; ");	}	public void execAssign() {		id.val = exp.execExp();		id.initialized = true;	}		}